import React from "react"

class MyItem extends React.Component {
	constructor() {
		super();

		this.state = {
			like: false
		};
	}

	render() {
	const {person, removePerson, addPersonToLikes} = this.props;
		return (
			<div className="card">
				<img 
					className="card-img-top h-500"
					src={person.photo}
					alt=""
				/>
				<div className="card-body">
					<h6 className="card-title">{person.name}</h6>
	       			<div className="d-flex justify-content-between align-item-center">

		       			<button 
		       				type="button" 
		       				onClick={removePerson.bind(null, person)}>
		       			  		Delete person
		       			</button>
		       			<button 
		       				type="button" 
		       				className={this.state.like ? "btn btn-success" : "btn btn-secondary"}
		       				onClick={
		       					this.setState({
		       						like: !this.state.like
		       					})
		       					// addPersonToLikes.bind(null, person)
		       				}>
		       			  		Like
		       			</button>
		       		</div>
		       	</div>
       		 </div>
		);
	}

}



