import React from "react";
import ReactDOM from "react-dom";
import "./styles.css";
import peopleData from "./people.js"

function Image(props) {
  return <img width="300px" src={props.src} alt="" />;
}

class MyItem extends React.Component {
  constructor() {
    super();

    this.state = {
      people: peopleData,
    };
  }

  removePerson(person) {
  	const updatePeople = this.state.people.filter(function(item){
  		return item.id !== person.id;
  	});

  	this.setState({
  		people: updatePeople
  	});
  }

  render() {
    return (
      <div className={"container"}>
        {this.state.people.map(person => {
        	return (
        		<div key={person.id}>
        			<p>{person.name}</p>
        			<button type="button" onClick={this.removePerson.bind(this, person)}>Delete person</button>
        		</div>
        	);
        })}
      </div>
    );
  }
}

export default function App() {
  return (
    <div className="App">
      <MyItem />
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById("root"));
